import React, { useContext } from "react";
import { GetCountryInfo } from "./GetCountryInfo";

function Country_Info({ info }) {
  const { countryName } = useContext(GetCountryInfo);
  console.log(info);
  return info ? (
    <div>
      <div>
        <img src={info.flag} alt={info.name + "flag"} />
      </div>
      <div>
        <h3>{info.name}</h3>
        <div>
          <p>Native Name:{info.nativeName}</p>
          <p>Population:{Number(info.population).toLocaleString()}</p>
          <p>Region:{info.region}</p>
          <p> Sub Region: {info.subregion}</p>
          <p>Capital: {info.capital} </p>

          <p c>
            Currencies:
            {info.currencies.map((item) => `${item.name} (${item.symbol})`)}
          </p>
          <p>
            Languages:
            {info.languages.map((item) => item.name).join(",")}
          </p>
        </div>
      </div>
    </div>
  ) : (
    <div>LOADING</div>
  );
}
export default Country_Info;
